import org.apache.nifi.processor.io.StreamCallback
import groovy.json.JsonSlurper
import groovy.json.JsonOutput
import java.nio.charset.StandardCharsets
import java.time.*
import java.time.format.DateTimeFormatter

def flowFile = session.get()
if (!flowFile) return

flowFile = session.write(flowFile, { inputStream, outputStream ->

    def json = new JsonSlurper().parseText(inputStream.getText('UTF-8'))
    def result = []

    def zone = ZoneId.of("Asia/Kolkata")
    def fmt = DateTimeFormatter.ofPattern("yyyy-MM-dd HH:mm:ss").withZone(zone)
    def now = Instant.now()

    // FIXED start & end time (IST)
    def today = ZonedDateTime.now(zone)
    def fixedStart = today.withHour(12).withMinute(0).withSecond(0).withNano(0)
    def fixedEnd   = today.withHour(23).withMinute(59).withSecond(59).withNano(0)

    json.each { record ->

        record.stats?.each { stat ->

            def ts = (stat.startTime ?: record.startTime) as Long
            def hourTime = Instant.ofEpochMilli(ts).atZone(zone)
            def hourStr = fmt.format(hourTime.withMinute(0).withSecond(0).withNano(0))

            //  ENTRY ONLY  removed exitStats logic
            def entrySum = stat.entryStats?.sum ?: 0

            result << [
                hour        : hourStr,
                entry_sum   : entrySum,                //  ONLY entry value
                location_id : stat.locationid,
                start_time  : fmt.format(fixedStart),  // 12:00:00 IST
                end_time    : fmt.format(fixedEnd),    // 23:59:59 IST
                created_at  : fmt.format(now),
                updated_at  : fmt.format(now)
            ]
        }
    }

    outputStream.write(
        JsonOutput.prettyPrint(JsonOutput.toJson(result))
            .getBytes(StandardCharsets.UTF_8)
    )

} as StreamCallback)

session.transfer(flowFile, REL_SUCCESS)

