import org.apache.nifi.processor.io.StreamCallback
import groovy.json.JsonSlurper
import groovy.json.JsonOutput
import java.nio.charset.StandardCharsets
import java.io.ByteArrayOutputStream

class FlattenCallback implements StreamCallback {
    def result = []
    def start_time
    def end_time

    FlattenCallback(start_time, end_time) {
        this.start_time = start_time
        this.end_time = end_time
    }

    @Override
    void process(InputStream inputStream, OutputStream outputStream) throws IOException {
        def json = new JsonSlurper().parse(inputStream)
        def touchpoint = json?._source?.touchpoint
        def hours = json?._source?.hours

        hours?.each { hourKey, hourValue ->
            hourValue?.locations?.each { loc ->
                if (loc?.locationId && touchpoint != null) {
                    result << [
                        touchpoint : touchpoint,
                        locationid : loc.locationId,
                        active     : loc.active,
                        start_time : start_time,
                        end_time   : end_time
                    ]
                }
            }
        }

        //  Filter out empty maps
        result = result.findAll { it && !it.isEmpty() }

        //  Write only if not empty
        if (result && !result.isEmpty()) {
            outputStream.write(JsonOutput.toJson(result).getBytes(StandardCharsets.UTF_8))
        }
    }
}

flowFile = session.get()
if (!flowFile) return

//  Read start_time and end_time from FlowFile attributes
def start_time = flowFile.getAttribute('start_time')
def end_time = flowFile.getAttribute('end_time')

// Step 1: Write flattened JSON with time attributes
flowFile = session.write(flowFile, new FlattenCallback(start_time, end_time))

// Step 2: Read back content for validation
def contentStream = new ByteArrayOutputStream()
session.exportTo(flowFile, contentStream)
def contentString = new String(contentStream.toByteArray(), StandardCharsets.UTF_8).trim()

// Step 3: Handle empty output
if (!contentString || contentString == "[]" || contentString == "[ ]") {
    session.remove(flowFile)
} else {
    flowFile = session.putAttribute(flowFile, "mime.type", "application/json")
    session.transfer(flowFile, REL_SUCCESS)
}
