import org.apache.nifi.processor.io.StreamCallback
import groovy.json.JsonSlurper
import groovy.json.JsonOutput
import java.nio.charset.StandardCharsets
import java.time.*
import java.time.format.DateTimeFormatter
import java.time.Instant
import java.time.ZoneId

flowFile = session.get()
if(!flowFile) return

flowFile = session.write(flowFile, { inputStream, outputStream ->

    def text = inputStream.getText("UTF-8")
    def json = new JsonSlurper().parseText(text)

    // --- READ ATTRIBUTES ---
    def startTimeAttr = flowFile.getAttribute("datetimerange-fromTime")?.trim()
    def endTimeAttr   = flowFile.getAttribute("datetimerange-toTime")?.trim()
	def trafficType   = flowFile.getAttribute("trafficType")

	// Convert epoch string  yyyy-MM-dd HH:mm:ss in Asia/Kolkata timezone
	def toISTDateTime = { epochStr ->
		try {
			def epoch = Long.parseLong(epochStr)
			if (epoch < 1000000000000L) {
				epoch *= 1000L
			}
			def zoned = Instant.ofEpochMilli(epoch).atZone(ZoneId.of("Asia/Kolkata"))
			return zoned.format(DateTimeFormatter.ofPattern("yyyy-MM-dd HH:mm:ss"))
		} catch (Exception e) {
			return epochStr
		}
	}

	def formattedStartTime = toISTDateTime(startTimeAttr)
	def formattedEndTime = toISTDateTime(endTimeAttr)

	//  Current timestamp in Asia/Kolkata timezone
	def nowKolkata = java.time.ZonedDateTime.now(java.time.ZoneId.of("Asia/Kolkata"))
	def formatter = java.time.format.DateTimeFormatter.ofPattern("yyyy-MM-dd HH:mm:ss")
	def currentTime = nowKolkata.format(formatter)

    // --- Add to JSON ---
    json["start_time"] = formattedStartTime
    json["end_time"]   = formattedEndTime
	json["trafficType"]   = trafficType

    outputStream.write(JsonOutput.prettyPrint(JsonOutput.toJson(json)).getBytes(StandardCharsets.UTF_8))

} as StreamCallback)

session.transfer(flowFile, REL_SUCCESS)

