import org.apache.nifi.processor.io.StreamCallback
import groovy.json.JsonSlurper
import groovy.json.JsonOutput
import java.time.Instant
import java.time.ZoneId
import java.time.format.DateTimeFormatter

def flowFile = session.get()
if(!flowFile) return

// Timestamp conversion function
def toTimestamp = { v ->
    if(v == null) return null
    try {
        return Instant.ofEpochMilli(v).atZone(ZoneId.of("UTC"))
                .format(DateTimeFormatter.ofPattern("yyyy-MM-dd HH:mm:ss"))
    } catch(e) { return null }
}

flowFile = session.write(flowFile, { inputStream, outputStream ->

    def json = new JsonSlurper().parse(inputStream)
    def rows = []

    json.each { entry ->
        def timestamp = toTimestamp(entry.timestamp)

        entry.stats.each { airline, statsObj ->

            // Include all flight buckets
            ['delayedFlights', 'onTimeFlights', 'earlyFlights'].each { listKey ->

                if (statsObj[listKey] instanceof List) {
                    statsObj[listKey].each { f ->

                        def row = [:]

                        // Rename to match DB columns
                        row.airline_code = airline
                        row.movement_indicator = f.movementIndicator
                        row.traffic_type = f.trafficType

                        // OTP value
                        row.otp = statsObj.otp

                        //  Same timestamp for all date fields
                        row.crt_date = timestamp
                        row.mod_date = timestamp
                        row.start_time = timestamp
                        row.end_time = timestamp

                        // Removed flight_no and registration
                        // row.flight_no = f.flightNo
                        // row.registration = f.registration

                        rows << row
                    }
                }
            }
        }
    }

    outputStream.write(JsonOutput.toJson(rows).getBytes("UTF-8"))
} as StreamCallback)

session.transfer(flowFile, REL_SUCCESS)
