import org.apache.nifi.processor.io.StreamCallback
import groovy.json.JsonSlurper
import groovy.json.JsonOutput
import java.nio.charset.StandardCharsets
import java.util.UUID
import java.time.Instant
import java.time.ZoneId
import java.time.format.DateTimeFormatter

// Define formatter for hours
def formatter = DateTimeFormatter.ofPattern("MM/dd/yyyy H:mm").withZone(ZoneId.of("UTC"))

// Wrap NiFi FlowFile handling
def flowFile = session.get()
if (!flowFile) return

flowFile = session.write(flowFile, { inputStream, outputStream ->
    // Read FlowFile content (assume it's JSON)
    def text = inputStream.getText(StandardCharsets.UTF_8)
    def parser = new JsonSlurper()
    def data = parser.parseText(text)

    // Fetch start_time and end_time from FlowFile attributes
    def startTime = flowFile.getAttribute("startTime")?.toLong()
    def endTime = flowFile.getAttribute("endTime")?.toLong()

    if (!startTime || !endTime) {
        throw new IllegalArgumentException("FlowFile missing startTime or endTime attributes")
    }

    def transformed = []

    data.each { parent ->
        parent.stats.each { stat ->
            // Convert startTime millis to formatted hours string
            def hourFormatted = formatter.format(Instant.ofEpochMilli(stat.startTime))

            def entrySum = (stat.entryStats?.sum ?: 0) + (stat.exitStats?.sum ?: 0)
            def compositeKey = "${stat.locationid}_${hourFormatted}"
            def id = UUID.randomUUID().toString()  // Generate unique ID

            transformed << [
                id         : id,
                primary_key: compositeKey,
                locationid : stat.locationid,
                hours      : hourFormatted,
                entry_sum  : entrySum,
                start_time : formatter.format(Instant.ofEpochMilli(startTime)),
                end_time   : formatter.format(Instant.ofEpochMilli(endTime))
            ]
        }
    }

    // Write transformed JSON back to FlowFile
    def outputJson = JsonOutput.prettyPrint(JsonOutput.toJson(transformed))
    outputStream.write(outputJson.getBytes(StandardCharsets.UTF_8))

} as StreamCallback)

session.transfer(flowFile, REL_SUCCESS)
