import org.apache.nifi.processor.io.StreamCallback
import groovy.json.JsonSlurper
import groovy.json.JsonOutput
import java.nio.charset.StandardCharsets
import java.time.*
import java.time.format.DateTimeFormatter

flowFile = session.get()
if(!flowFile) return

flowFile = session.write(flowFile, { inputStream, outputStream ->

    def inputText = inputStream.getText("UTF-8")
    def slurper = new JsonSlurper()

    // Main API response (array of locations)
    def apiResponse = slurper.parseText(inputText)

    // Extract start/end epoch from attributes (already in milliseconds)
    long startEpoch = flowFile.getAttribute("startTime").toLong()
    long endEpoch   = flowFile.getAttribute("endTime").toLong()

    // Convert start/end epoch to IST formatted timestamp
    ZoneId IST = ZoneId.of("Asia/Kolkata")
    def formatter = DateTimeFormatter.ofPattern("yyyy-MM-dd HH:mm:ss")

    String startTimeStr = Instant.ofEpochMilli(startEpoch)
                                .atZone(IST)
                                .format(formatter)

    String endTimeStr = Instant.ofEpochMilli(endEpoch)
                                .atZone(IST)
                                .format(formatter)

    def finalRows = []

    apiResponse.each { loc ->
        def locationId = loc.locationId

        loc.hours.each { hr ->

            // Convert hour epoch  IST
            long hourEpoch = (hr.startTime as Long)
            String hourStr = Instant.ofEpochMilli(hourEpoch)
                                    .atZone(IST)
                                    .format(formatter)

            // Sanitizing entry
            def entryVal = 0
            try {
                entryVal = (hr.entry != null) ? hr.entry.toLong() : 0
            } catch(Exception e) {
                entryVal = 0
            }

            def row = [
                start_time  : startTimeStr,
                end_time    : endTimeStr,
                hour        : hourStr,
                entry_sum   : entryVal,
                location_id : locationId,
                domestic    : hr.domestic
            ]

            finalRows << row
        }
    }

    outputStream.write(JsonOutput.toJson(finalRows).getBytes(StandardCharsets.UTF_8))

} as StreamCallback)

session.transfer(flowFile, REL_SUCCESS)

