import org.apache.nifi.processor.io.StreamCallback
import groovy.json.JsonSlurper
import groovy.json.JsonOutput
import java.time.Instant
import java.time.ZoneId
import java.time.format.DateTimeFormatter

def flowFile = session.get()
if (!flowFile) return

// Read startTime and endTime directly from attributes
def startTimeAttr = flowFile.getAttribute('startTime')?.trim()
def endTimeAttr = flowFile.getAttribute('endTime')?.trim()

// Convert epoch string  yyyy-MM-dd HH:mm:ss in Asia/Kolkata timezone
def toISTDateTime = { epochStr ->
    try {
        def epoch = Long.parseLong(epochStr)
        if (epoch < 1000000000000L) {
            epoch *= 1000L
        }
        def zoned = Instant.ofEpochMilli(epoch).atZone(ZoneId.of("Asia/Kolkata"))
        return zoned.format(DateTimeFormatter.ofPattern("yyyy-MM-dd HH:mm:ss"))
    } catch (Exception e) {
        return epochStr
    }
}

def formattedStartTime = toISTDateTime(startTimeAttr)
def formattedEndTime = toISTDateTime(endTimeAttr)

//  Current timestamp in Asia/Kolkata timezone
def nowKolkata = java.time.ZonedDateTime.now(java.time.ZoneId.of("Asia/Kolkata"))
def formatter = java.time.format.DateTimeFormatter.ofPattern("yyyy-MM-dd HH:mm:ss")
def currentTime = nowKolkata.format(formatter)

flowFile = session.write(flowFile, { inputStream, outputStream ->

    def json = new JsonSlurper().parse(inputStream)
    def rows = []

    json.each { entry ->
        entry.stats.each { airline, statsObj ->

            // Get all flight lists
            def delayedList = statsObj.delayedFlights ?: []
            def ontimeList = statsObj.onTimeFlights ?: []
            def earlyList = statsObj.earlyFlights ?: []

            // Collect distinct combinations of movementIndicator and trafficType
            def combinations = []
            (delayedList + ontimeList + earlyList).each { f ->
                def key = "${f.movementIndicator}|${f.trafficType}"
                combinations << key
            }
            combinations = combinations.unique()

            combinations.each { combo ->
                def (movementIndicator, trafficType) = combo.split("\\|")

                // Count number of flightNo under each category for this combo
                def delayedCnt = delayedList.count { it.movementIndicator == movementIndicator && it.trafficType == trafficType && it.flightNo }
                def ontimeCnt = ontimeList.count { it.movementIndicator == movementIndicator && it.trafficType == trafficType && it.flightNo }
                def earlyCnt = earlyList.count { it.movementIndicator == movementIndicator && it.trafficType == trafficType && it.flightNo }

                // Prepare one row per airline / movement / traffic
                def row = [:]
                row.airline_code = airline
                row.movement_indicator = movementIndicator
                row.traffic_type = trafficType
                row.otp = statsObj.otp
                row.start_time = formattedStartTime
                row.end_time = formattedEndTime
                row.delayed_cnt = delayedCnt
                row.ontime_cnt = ontimeCnt
                row.early_cnt = earlyCnt

                rows << row
            }
        }
    }

    outputStream.write(JsonOutput.toJson(rows).getBytes("UTF-8"))
} as StreamCallback)

session.transfer(flowFile, REL_SUCCESS)

