import org.apache.nifi.processor.io.StreamCallback
import groovy.json.JsonSlurper
import groovy.json.JsonOutput
import java.nio.charset.StandardCharsets
import java.time.Instant
import java.time.ZoneId
import java.time.ZonedDateTime
import java.time.format.DateTimeFormatter

def flowFile = session.get()
if (!flowFile) return

def istZone = ZoneId.of("Asia/Kolkata")
def formatter = DateTimeFormatter.ofPattern("yyyy-MM-dd HH:mm:ss")

// Compute "today" in IST once
def now = ZonedDateTime.now(istZone)
def startTimeStr = now.withHour(0).withMinute(0).withSecond(1).withNano(0).format(formatter)
def endTimeStr   = now.withHour(23).withMinute(59).withSecond(59).withNano(0).format(formatter)
def createdAtStr = now.format(formatter)

flowFile = session.write(flowFile, { inputStream, outputStream ->

    def jsonText = inputStream.getText('UTF-8')
    def data = new JsonSlurper().parseText(jsonText)
    def transformed = []

    data.each { record ->
        // Convert timestamp to long
        def timestampMs = (record.timestamp as BigDecimal).longValue()
        def zdtEvent = Instant.ofEpochMilli(timestampMs).atZone(istZone)
        def eventTime = zdtEvent.format(formatter)

        record.stats.each { stat ->
            transformed << [
                start_time: startTimeStr,   // Always today IST
                end_time: endTimeStr,       // Always today IST
                timestamp_ms: timestampMs,
                event_time: eventTime,      // Event time in IST
                queue_id: stat.queueId ?: "",
                site_id: stat.siteId ?: "",
                zone_id: stat.zoneId ?: "",
                count: stat.count ?: 0,
                wait_time: stat.waitTime ?: 0,
                active_servers: stat.activeServers ?: 0,
                active_gates: stat.activeGates ?: 0,
                avg_server_service_time: stat.avgServerServiceTime ?: 0,
                avg_gate_service_time: stat.avgGateServiceTime ?: 0,
                entry_count: stat.entry ?: 0,
                exit_count: stat.exit ?: 0,
                created_at: createdAtStr
            ]
        }
    }

    outputStream.write(JsonOutput.prettyPrint(JsonOutput.toJson(transformed)).getBytes(StandardCharsets.UTF_8))

} as StreamCallback)

session.transfer(flowFile, REL_SUCCESS)
